import React, { useState } from "react";
import {
  BrowserRouter,
  Routes,
  Route,
  Link,
  useLocation,
} from "react-router-dom";
import App from "../components/App";
import Header from "../components/Header";
import FilesList from "../components/FilesList";
import { GoogleOAuthProvider } from "@react-oauth/google";
import Loginwithgoogle from "../components/LoginWithGoogle";

const AppRouter = () => {
  const [isLoggedin, setLoggedin] = useState(false);

  const handleLogin = () => {
    setLoggedin(true);
  };

  const handleLogout = () => {
    setLoggedin(false);
  };

  const HeaderWithConditionalRendering = () => {
    const location = useLocation();

    if (location.pathname === "/home") {
      return null;
    } else if (location.pathname === "/") {
      return (
        <nav>
          <ul>
            <li>Logging in...</li>
          </ul>
        </nav>
      );
    } else {
      return (
        <Header>
          <nav>
            <ul>
              <li>
                <Link to="/home">Home</Link>
              </li>
              <li>
                <Link to="/list" disabled={!isLoggedin}>
                  File List
                </Link>
              </li>
              <li>
                <button onClick={handleLogout}>Logout</button>
              </li>
            </ul>
          </nav>
        </Header>
      );
    }
  };

  return (
    <GoogleOAuthProvider clientId="1004046358549-4he79a4vc7os6t8g3agc299t6r1j3ndm.apps.googleusercontent.com">
      <BrowserRouter>
        <div className="container">
          <HeaderWithConditionalRendering />
          <div className="main-content">
            <Routes>
              <Route
                path="/"
                element={<Loginwithgoogle onLogin={handleLogin} />}
              />
              <Route path="/home" element={<App />} />
              <Route
                path="/list"
                element={
                  isLoggedin ? <FilesList /> : <p>You are not logged in</p>
                }
              />
            </Routes>
          </div>
        </div>
      </BrowserRouter>
    </GoogleOAuthProvider>
  );
};

export default AppRouter;
