// clientId="24075e83d5cf8bec239a"

import React, { useState } from "react";
import { Button, Heading } from "@primer/react";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Logo = styled.img`
  width: 80px;
  height: 80px;
  margin-bottom: 32px;
`;

const LoginButton = styled(Button)`
  width: 200px;
`;

const LoginWithGithub = () => {
  const [isLoading, setIsLoading] = useState(false);

  const handleLogin = () => {
    setIsLoading(true);

    // Replace the client_id with your own GitHub OAuth App client ID
    const clientId = "24075e83d5cf8bec239a";
    const redirectUri = window.location.origin;
    const scope = "user";

    const url = `https://github.com/login/oauth/authorize?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${scope}`;

    window.location.href = url;
  };

  return (
    <Container>
      <Logo
        src="https://github.githubassets.com/images/modules/logos_page/GitHub-Mark.png"
        alt="GitHub logo"
      />
      <Heading>Login with GitHub</Heading>
      <LoginButton onClick={handleLogin} isLoading={isLoading}>
        {isLoading ? "Logging in..." : "Login with GitHub"}
      </LoginButton>
    </Container>
  );
};

export default LoginWithGithub;
