(args) => {
  const [isLoading, setIsLoading] = React.useState(false)
  const [asyncItems, setAsyncItems] = React.useState<string[]>([])
  const [error, setError] = React.useState<Error | null>(null)
  let state: SubTreeState = 'initial'
  if (isLoading) {
    state = 'loading'
  } else if (error) {
    state = 'error'
  } else if (asyncItems.length > 0) {
    state = 'done'
  }
  async function loadItems() {
    if (asyncItems.length === 0) {
      setIsLoading(true)
      try {
        // Try to load items
        const items = await alwaysFails(args.responseTime)
        setAsyncItems(items)
      } catch (error) {
        setError(error as Error)
      } finally {
        setIsLoading(false)
      }
    }
  }
  return (
    <TreeView aria-label="Files">
      <TreeView.Item id="some-file">
        <TreeView.LeadingVisual>
          <FileIcon />
        </TreeView.LeadingVisual>
        Some file
      </TreeView.Item>
      <TreeView.Item
        id="async-directory"
        onExpandedChange={(isExpanded) => {
          if (isExpanded) {
            loadItems()
          }
        }}
      >
        <TreeView.LeadingVisual>
          <TreeView.DirectoryIcon />
        </TreeView.LeadingVisual>
        Directory with async items
        <TreeView.SubTree state={state}>
          {error ? (
            <TreeView.ErrorDialog
              onRetry={() => {
                setError(null)
                loadItems()
              }}
              onDismiss={() => {
                setError(null)
              }}
            >
              {error.message}
            </TreeView.ErrorDialog>
          ) : null}
          {asyncItems.map((item) => (
            <TreeView.Item key={item} id={`item-${item}`}>
              <TreeView.LeadingVisual>
                <FileIcon />
              </TreeView.LeadingVisual>
              {item}
            </TreeView.Item>
          ))}
